    width: "200px",
    border: "5px solid #932a33",
    boxShadow: "0px 2px 20px rgba(0, 0, 0, 0.2)",
    zIndex: 10,
  };

  const alignCenter = {
    display: "flex",
    alignItems: "center",
    // height: "100%",
    fontStyle: "italic",
    paddingTop: "30px",
