import React from "react";

const Changeheading = (props) => {
  const input = {
    display: "block",
    textAlign: "left",
    padding: "5px",
    borderRadius: "5px",
    border: "0",
  };
  return (
    <div>
      <input
        style={input}
        type="text"
        onChange={props.changed}
        placeholder="Update Name"
      />
    </div>
  );
};

export default Changeheading;
