import React, { useState } from "react"; //// implementing react hooks
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import imgSrc from "../images/usa.jpg";

const Cards = (props) => {
  /* inline css */
  const storyHeading = {
    textAlign: "center",
    fontSize: "40px",
    letterSpacing: "2px",
    borderBottom: "1px solid #dedede",
    paddingBottom: "5px",
    color: "#932a33",
    fontWeight: "600",
    marginBottom: "50px",
  };
  const buttonStyle = {
    display: "flex",
    padding: "15px",
    border: "none",
    backgroundColor: "rgb(147, 42, 51)",
    color: "white",
    borderRadius: "5px",
    boxShadow: "0px 2px 20px rgba(0, 0, 0, 0.2)",
  };
  const paraStyle = {
    textAlign: "justify",
  };
  const imgStyle = {
    width: "100%",
    height: "370px",
    boxShadow: "0px 2px 20px rgba(0, 0, 0, 0.3)",
    borderRadius: "5px",
  };
  /* inline css ends */

  // const [paragraphState] = useState({
  //   para: [
  //     {
  //       paraData:
  //         "Lorem ipsum dolor sit amet consectetur adipisicing elit. Lorem ipsum dolor sit amet consectetur adipisicing elit. Lorem ipsum dolor sit amet consectetur adipisicing elit",
  //     },
  //   ],
  // });

  // updateuserName = () => {
  //   this.setState({
  //     output: [{ name: "newName" }],
  //   });
  // };

  return (
    <div className="wrap-around">
      <h1 style={storyHeading}>{props.storyHeading}</h1>
      <div className="row">
        <Col md={8}>
          <div className="content-wrapper">
            <p style={paraStyle}>
              Lorem ipsum dolor sit amet, consectetur adipisicing elit.
              Laudantium ad placeat a eaque et voluptatem possimus debitis
              blanditiis quae, nesciunt est, omnis vel. Sit qui sapiente,
              voluptate sint nisi velit! Lorem ipsum dolor sit amet, consectetur
              adipisicing elit. Eligendi numquam laudantium dignissimos
              inventore aliquid ab in quos assumenda, unde animi eos, fugit nam
              necessitatibus ipsam consequuntur iste sint est illo.
            </p>
            <button type="button" style={buttonStyle}>
              Update Paragraph
            </button>
          </div>
        </Col>
        <Col md={4}>
          <div className="card-img">
            <img
              src={imgSrc}
              alt="usa"
              className="img-fluid"
              style={imgStyle}
            />
          </div>
        </Col>
      </div>
    </div>
  );
};

export default Cards;
