import React from "react";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import imgSrc from "../images/usa.jpg";

const Cards = (props) => {
  const storyHeading = {
    textAlign: "center",
    fontSize: "40px",
    letterSpacing: "2px",
    borderBottom: "1px solid #dedede",
    paddingBottom: "5px",
    color: "#932a33",
    fontWeight: "600",
    marginBottom: "50px",
  };
  const buttonStyle = {
    display: "flex",
    padding: "15px",
    border: "none",
  };
  const paraStyle = {
    textAlign: "justify",
  };
  const imgStyle = {
    width: "100%",
    height: "370px",
    boxShadow: "0px 2px 20px rgba(0, 0, 0, 0.3)",
    borderRadius: "5px",
  };
  return (
    <div className="wrap-around">
      <h1 style={storyHeading}>{props.storyHeading}</h1>
      <div className="row">
        <Col md={8}>
          <div className="content-wrapper">
            <p style={paraStyle}>
              Lorem ipsum dolor sit amet consectetur adipisicing elit. Veritatis
              repellat dignissimos, incidunt iusto nulla facere deserunt
              obcaecati temporibus minus officia ratione, esse odit dolore est
              alias inventore, maxime quae quasi at dolorem ducimus magnam?
              Mollitia veritatis, quae repellat esse, quia totam consectetur
              ratione molestiae iusto, in eveniet quidem. Labore soluta ut
              error, totam nesciunt illo laborum? Incidunt sapiente eligendi vel
              quidem odio amet quasi, non minus vitae iusto asperiores cumque
              sunt omnis optio tempore, consectetur ullam aliquid. Itaque id et
              beatae sint recusandae at rerum iste! Quia itaque cupiditate neque
              nisi iure facere harum, tenetur architecto deleniti accusantium
              distinctio, voluptatem quasi fuga accusamus quam amet atque dolor
              error sequi? Laboriosam maiores a aliquid quis consectetur ipsum
              eaque! Omnis libero ducimus obcaecati veniam voluptates culpa?
              Aperiam aliquam dicta ullam consequuntur porro numquam consectetur
              deleniti asperiores! Esse illo enim quod, dolore quaerat labore
              nesciunt dignissimos iure obcaecati laboriosam natus doloremque
              veritatis minima.
            </p>
            <button type="button" style={buttonStyle}>
              Update Paragraph
            </button>
          </div>
        </Col>
        <Col md={4}>
          <div className="card-img">
            <img
              src={imgSrc}
              alt="usa"
              className="img-fluid"
              style={imgStyle}
            />
          </div>
        </Col>
      </div>
    </div>
  );
};

export default Cards;
