import Jumbotron from "react-bootstrap/Jumbotron";
import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import React, { Component, useRef, useEffect } from "react";
import Changeheading from "./changetext";
/* gsap imports */
import gsap, { Power2 } from "gsap";
import ScrollTrigger from "gsap/ScrollTrigger";
gsap.registerPlugin(ScrollTrigger);

class Maincontent extends Component {
  state = {
    update: [{ name: "Hello World!" }],
    updateName: true,
  };
  toggleHeading = () => {
    this.setState({
      update: [{ name: "Jumbotron" }],
    });
  };
  changeText = (nameChanged) => {
    this.setState({
      update: [{ name: nameChanged.target.value }],
    });
  };

  render() {
    let selectHeading = useRef();
    let selectPara = useRef();
    let selectButton = useRef();
    let selectInput = useRef();

    useEffect(() => {
      const animateHeading = gsap.timeline();
      const animatePara = gsap.timeline();
      const animateButton = gsap.timeline();
      const animateInput = gsap.timeline();
      animateHeading.from(selectHeading, {
        y: -100,
        opacity: 0,
        ease: Power2.easeInOut,
        duration: 0.1,
        delay: 0.2,
      });
      animatePara.from(selectPara, {
        y: -100,
        opacity: 0,
        ease: Power2.easeInOut,
        duration: 0.1,
        delay: 0.2,
      });
    }, []);
    let Newupdate = false;
    if (this.state.updateName) {
      Newupdate = (
        <div>
          <h1 className="display-3">{this.state.update[0].name}</h1>
        </div>
      );
    }
    return (
      <div>
        <Jumbotron className="custom-jumbotron">
          <Container>
            {Newupdate}
            <p className="lead">
              This is a simple hero unit, a simple jumbotron-style component for
              calling extra attention to featured content or information.
            </p>
            <p className="lead">
              <Button variant="primary" onClick={this.toggleHeading}>
                Update Heading
              </Button>
            </p>
            <Changeheading changed={this.changeText}></Changeheading>
          </Container>
        </Jumbotron>
      </div>
    );
  }
}

export default Maincontent;
