import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Useroutput from "./components/useroutput";
import Userinput from "./components/userinput";
import Jumbotron from "./components/jumbotron";
import "./App.css";
import "./components/user.css";

class App extends Component {
  state = {
    output: [{ name: "Chandan" }, { name: "Rishiraj" }],
  };
  updateuserName = (newName) => {
    this.setState({
      output: [{ name: newName }, { name: "Rishiraj" }],
    });
  };

  namechangedHandler = (updateName) => {
    this.setState({
      output: [
        { name: updateName.target.value },
        { name: updateName.target.value },
      ],
    });
  };
  render() {
    const wrapper = {
      margin: "50px 0",
      padding: "20px",
      border: "1px solid #eee",
    };
    const button = {
      border: "1px solid #eee",
      backgroundColor: "#fff",
      font: "inherit",
      borderRadius: "3px",
      padding: "5px",
      width: "60%",
      fontSize: "15px",
    };
    return (
      <div className="App">
        <Jumbotron fluid>
          <Container>
            <h1>Fluid jumbotron</h1>
            <p>
              This is a modified jumbotron that occupies the entire horizontal
              space of its parent.
            </p>
          </Container>
        </Jumbotron>
        ;
        <Container>
          <div style={wrapper}>
            <Row>
              <Useroutput
                username={this.state.output[0].name}
                paragraph="Hey There! Wassup"
              >
                <button
                  style={button}
                  type="button"
                  onClick={this.updateuserName.bind(this, "Rishi")}
                >
                  Update Username
                </button>
              </Useroutput>
              <Useroutput
                username={this.state.output[1].name}
                paragraph="Hey There! Wassup"
              />
              <Userinput changed={this.namechangedHandler} />
            </Row>
          </div>
        </Container>
      </div>
    );
  }
}

export default App;
