import React from "react";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

const Cards = (props) => {
  const storyHeading = {
    textAlign: "center",
    fontSize: "40px",
    letterSpacing: "2px",
    borderBottom: "1px solid #dedede",
    paddingBottom: "5px",
    color: "#932a33",
    fontWeight: "600",
    marginBottom: "50px",
  };
  const paraStyle = {
    textAlign: "left",
  };
  const imgStyle = {
    width: "100%",
    height: "370px",
  };
  return (
    <div className="wrap-around">
      <h1 style={storyHeading}>{props.storyHeading}</h1>
      <div className="row">
        <Col md={8}>
          <div className="content-wrapper">
            <p style={paraStyle}>
              Lorem ipsum dolor sit amet consectetur adipisicing elit.
              Laboriosam fuga ea iste omnis fugit nemo aliquid. Minima, tempore
              fugit quaerat deserunt vero asperiores quia dignissimos nostrum ea
              laudantium placeat sunt.
            </p>
          </div>
        </Col>
        <Col md={4}>
          <div className="card-img">
            <img
              src={props.imgSrc}
              alt="usa"
              className="img-fluid"
              style={imgStyle}
            />
          </div>
        </Col>
      </div>
    </div>
  );
};

export default Cards;
