import React, { Component } from "react";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";

class Mycorner extends Component {
  state = {
    firstPart: "Hola",
    secondPart: "Amigo",
    showHeading: true,
  };

  SwitchNames = () => {
    this.setState({
      firstPart: "Hello",
    });
  };

  render() {
    const addSpacing = {
      paddingLeft: "10px",
      color: "#932a33",
    };
    let Headingname = null;
    if (this.state.showHeading) {
      Headingname = (
        <h1 onClick={this.SwitchNames}>
          {this.state.firstPart}
          <span style={addSpacing}>{this.state.secondPart}</span>
        </h1>
      );
    }
    return (
      <>
        <section>{Headingname}</section>
      </>
    );
  }
}

export default Mycorner;
