import React, { Component } from "react";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import mask1 from "../images/mask-1.jpg";
import project1 from "../images/projects/dogbox.png";
import project2 from "../images/projects/svg-animation.png";
import project3 from "../images/projects/syngene.png";
import project4 from "../images/projects/blackswan.png";

class Mycorner extends Component {
  state = {
    firstPart: "Success",
    secondPart: "Story",
    secPara:
      "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.",
    secPara1:
      "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.",
    showHeading: true,
    renderImage1: true,
    renderImage2: true,
    renderImage3: true,
    renderImage4: true,
  };

  render() {
    const addSpacing = {
      paddingLeft: "10px",
      color: "#932a33",
    };
    let Headingname = null;
    if (this.state.showHeading) {
      Headingname = (
        <h1 style={{ paddingBottom: "50px" }}>
          {this.state.firstPart}
          <span style={addSpacing}>{this.state.secondPart}</span>
        </h1>
      );
    }
    let image1 = null;
    let image2 = null;
    let image3 = null;
    let image4 = null;
    if (this.state.renderImage1) {
      image1 = <img src={project1} />;
    } else if (this.state.renderImage2) {
      image2 = <img src={project2} />;
    } else if (this.state.renderImage3) {
      image3 = <img src={project3} />;
    } else if (this.state.renderImage4) {
      image4 = <img src={project4} />;
    }
    return (
      <>
        <section style={{ padding: "50px 0" }}>
          {Headingname}
          <Row>
            <Col md={4}>
              <div>
                <p
                  style={{
                    textAlign: "justify",
                    wordSpacing: "-1px",
                    fontSize: "16px",
                    lineHeight: "25px",
                  }}
                >
                  {this.state.secPara}
                </p>
              </div>
            </Col>
            <Col md={4}>
              <img src={mask1} className="img-fluid" />
            </Col>
            <Col md={4}>
              <div>
                <p
                  style={{
                    textAlign: "justify",
                    wordSpacing: "-1px",
                    fontSize: "16px",
                    lineHeight: "25px",
                  }}
                >
                  {this.state.secPara1}
                </p>
              </div>
            </Col>
          </Row>
          <div className="more-content">
            <Row>
              <Col md={6}>
                <h1
                  style={{
                    textAlign: "left",
                    borderBottom: "1px solid #dedede",
                    color: "#932a33",
                  }}
                >
                  Portfolio
                </h1>
                <p style={{ textAlign: "left" }}>
                  Lorem ipsum dolor sit amet consectetur adipisicing elit.
                  Architecto quia minus dignissimos facilis recusandae delectus
                  eos culpa suscipit odit enim nobis error unde, cupiditate,
                  explicabo labore eius officiis fugiat. Assumenda.
                </p>
              </Col>
              <Col md={6}>
                <div className="img-wrapper">
                  {image1} {image1} {image1} {image1}
                </div>
              </Col>
            </Row>
          </div>
        </section>
      </>
    );
  }
}

export default Mycorner;
